// src/components/TaskPanel.jsx
import React from 'react';

const TaskPanel = ({ tasks }) => {
  return (
    <div className="task-panel">
      {tasks.length === 0 ? (
        <p>No tasks found.</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              <span>{task.title}</span>
              {task.status === 'completed' && <span> âœ…</span>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskPanel;
