import React, { useState } from 'react';
import { useMsal } from "@azure/msal-react";
import { loginRequest } from "./authConfig";
import TaskPanel from './TaskPanel';
import PreviewPanel from './PreviewPanel';

export default function App() {
  const { instance } = useMsal();
  const [group, setGroup] = useState('Group 1');
  const [panelLists, setPanelLists] = useState(['Work', 'Personal', 'Errands', 'Ideas', 'Projects', 'Misc']);
  const [selectedTask, setSelectedTask] = useState(null);

  const handleLogin = () => {
    instance.loginPopup(loginRequest).catch(e => {
      console.error(e);
    });
  };

  const handleSaveGroup = () => {
    localStorage.setItem(group, JSON.stringify(panelLists));
    alert(`Saved current setup for ${group}`);
  };

  const handleListChange = (index, newList) => {
    const updatedLists = [...panelLists];
    updatedLists[index] = newList;
    setPanelLists(updatedLists);
  };

  return (
    <div className="dashboard">
      <div className="top-bar">
        <div className="top-left">
          <button onClick={handleLogin}>Sign In</button>
        </div>
        <div className="top-center">
          <button onClick={handleSaveGroup}>Save Current Setup</button>
        </div>
        <div className="top-right">
          <select value={group} onChange={(e) => setGroup(e.target.value)}>
            <option>Group 1</option>
            <option>Group 2</option>
            <option>Group 3</option>
          </select>
        </div>
      </div>

      <div className="panel-grid">
        {panelLists.map((listName, index) =>
          index === 5 ? (
            <PreviewPanel key={index} task={selectedTask} />
          ) : (
            <TaskPanel
              key={index}
              listName={listName}
              onListChange={(newList) => handleListChange(index, newList)}
              onTaskSelect={setSelectedTask}
            />
          )
        )}
      </div>
    </div>
  );
}
