// src/components/TaskPanel.jsx
import React, { useState } from 'react';
import { useMsal } from '@azure/msal-react';
import { InteractionRequiredAuthError } from '@azure/msal-browser';

const TaskPanel = ({ tasks, onSelectTask, listId }) => {
  const { instance } = useMsal();
  const [newTask, setNewTask] = useState('');
  const [editedTitles, setEditedTitles] = useState({});

  const handleAddTask = async () => {
    if (!newTask.trim() || !listId) return;
    const account = instance.getActiveAccount();
    if (!account) return;

    let response;
    try {
      response = await instance.acquireTokenSilent({
        scopes: ['Tasks.ReadWrite'],
        account,
      });
    } catch (error) {
      if (error instanceof InteractionRequiredAuthError) {
        response = await instance.loginPopup({
          scopes: ['Tasks.ReadWrite'],
        });
        instance.setActiveAccount(response.account);
      } else {
        console.error('Error acquiring token:', error);
        return;
      }
    }

    await fetch(`https://graph.microsoft.com/v1.0/me/todo/lists/${listId}/tasks`, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${response.accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title: newTask }),
    });

    setNewTask('');
  };

  const handleCompleteTask = async (taskId) => {
    const account = instance.getActiveAccount();
    if (!account) return;

    let response;
    try {
      response = await instance.acquireTokenSilent({
        scopes: ['Tasks.ReadWrite'],
        account,
      });
    } catch (error) {
      if (error instanceof InteractionRequiredAuthError) {
        response = await instance.loginPopup({
          scopes: ['Tasks.ReadWrite'],
        });
        instance.setActiveAccount(response.account);
      } else {
        console.error('Error acquiring token:', error);
        return;
      }
    }

    await fetch(`https://graph.microsoft.com/v1.0/me/todo/lists/${listId}/tasks/${taskId}`, {
      method: 'PATCH',
      headers: {
        Authorization: `Bearer ${response.accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status: 'completed' }),
    });
  };

  const handleTitleChange = (taskId, newTitle) => {
    setEditedTitles((prev) => ({ ...prev, [taskId]: newTitle }));
  };

  const handleTitleBlur = async (taskId) => {
    const newTitle = editedTitles[taskId];
    if (!newTitle) return;

    const account = instance.getActiveAccount();
    if (!account) return;

    let response;
    try {
      response = await instance.acquireTokenSilent({
        scopes: ['Tasks.ReadWrite'],
        account,
      });
    } catch (error) {
      if (error instanceof InteractionRequiredAuthError) {
        response = await instance.loginPopup({
          scopes: ['Tasks.ReadWrite'],
        });
        instance.setActiveAccount(response.account);
      } else {
        console.error('Error acquiring token:', error);
        return;
      }
    }

    await fetch(`https://graph.microsoft.com/v1.0/me/todo/lists/${listId}/tasks/${taskId}`, {
      method: 'PATCH',
      headers: {
        Authorization: `Bearer ${response.accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title: newTitle }),
    });

    setEditedTitles((prev) => {
      const updated = { ...prev };
      delete updated[taskId];
      return updated;
    });
  };

  const incompleteTasks = tasks
    .filter((task) => task.status !== 'completed')
    .sort((a, b) => {
      const aTitle = a.title || '';
      const bTitle = b.title || '';
      const aIsBottom = aTitle.startsWith('üï≥Ô∏è');
      const bIsBottom = bTitle.startsWith('üï≥Ô∏è');
      if (aIsBottom && !bIsBottom) return 1;
      if (!aIsBottom && bIsBottom) return -1;
      return aTitle.localeCompare(bTitle);
    });

  return (
    <div>
      {incompleteTasks.length === 0 ? (
        <p style={{ fontSize: '0.8rem' }}>No tasks found.</p>
      ) : (
        <ul style={{ fontSize: '0.8rem' }}>
          {incompleteTasks.map((task) => (
            <li key={task.id}>
              <input
                type="checkbox"
                onChange={() => handleCompleteTask(task.id)}
                style={{ marginRight: '6px' }}
              />
              <input
                type="text"
                value={editedTitles[task.id] ?? task.title}
                onChange={(e) => handleTitleChange(task.id, e.target.value)}
                onBlur={() => handleTitleBlur(task.id)}
                style={{ fontSize: '0.8rem', width: '80%' }}
              />
            </li>
          ))}
        </ul>
      )}
      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && handleAddTask()}
        placeholder="Add new task"
        style={{ width: '100%', marginTop: '8px', fontSize: '0.8rem' }}
      />
    </div>
  );
};

export default TaskPanel;
