import React, { useState } from 'react';
import useTaskLists from './hooks/useTaskLists';
import useTasks from './hooks/useTasks';
import TaskListSelector from './components/TaskListSelector';
import TaskPanel from './components/TaskPanel';
import SignInButton from './components/SignInButton';
import PreviewPanel from './components/PreviewPanel';
import './styles.css'; // ✅ Ensure styles are loaded

const App = () => {
  const { taskLists, loading: loadingLists } = useTaskLists();
  const [selectedListId, setSelectedListId] = useState('');
  const { tasks, loading: loadingTasks } = useTasks(selectedListId);
  const [selectedTask, setSelectedTask] = useState(null);

  return (
    <div className="dashboard">
      <div className="top-bar">
        <div className="top-left">
          <SignInButton />
        </div>
        <div className="top-center">
          <button>Save Current Setup</button>
        </div>
        <div className="top-right">
          {loadingLists ? (
            <p>Loading lists...</p>
          ) : (
            <TaskListSelector
              taskLists={taskLists}
              selectedListId={selectedListId}
              onSelect={setSelectedListId}
            />
          )}
        </div>
      </div>

      <div className="panel-grid">
        {[...Array(6)].map((_, index) => (
          <div key={index} className="panel">
            {index === 5 ? (
              <PreviewPanel task={selectedTask} listId={selectedListId} />
            ) : (
              loadingTasks ? (
                <p>Loading tasks...</p>
              ) : (
                <TaskPanel tasks={tasks} onSelectTask={setSelectedTask} />
              )
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
